use egg_matching::ematch;

use std::time::Duration;

fn test(a: &str, pat: &str) {
    println!("Matching {} against {}", a, pat);
    let substs = ematch(a, pat, Duration::from_secs(5));
    println!("Found {} substitutions:", substs.len());
    for subst in &substs {
        for (k, v) in subst {
            print!("{} -> {}, ", k, v);
        }
        println!("")
    }
}


